INSERT INTO `wp_options` VALUES("490", "alterna_options-update-xml-file", "a:2:{s:4:\"date\";s:10:\"1437586479\";s:3:\"xml\";s:87675:\"<notifier>\n<latest>9.4.8</latest>\n<changelog><![CDATA[\n<style type=\"text/css\" scoped>\n.update-notice {margin: 0 0 10px;font-size: 18px;}\n.update-notice i {margin-right: 10px;}\n.update-notice-content {margin: 0 0 15px;}\n.update-content {margin-bottom: 20px;}\n.update-title {margin: 5px 0;font-size: 15px;border-bottom: 1px #f0f0f0 solid;padding-bottom: 5px;}\n.update-bullets {margin: 0;padding-left: 10px;border-left: 5px solid #e0e0e0;}\n.update-bullets li {padding:2px 0 2px 2px;margin:0;background: #f7f7f7;}\n.update-bullets li span {display: block;padding-left: 22px;color: #01adab;font-size: 11px;}\n.update-bullets li i {margin-right:6px;display: inline-block;width: 16px;height:16px;text-align: center;line-height:16px;}\n.update-bullets li.notice {background: #fef3f3;padding: 3px 0 3px 2px;}\n.update-bullets li i.icon-bullhorn,\n.update-bullets li i.fa-bullhorn {color:#f30000;}\n.update-bullets li i.icon-star,\n.update-bullets li i.fa-star,\n.update-bullets li.features {color:#f903a5;}\n.update-bullets li i.icon-refresh,\n.update-bullets li i.fa-refresh,\n.update-bullets li.updates {color:#f98e03;}\n.update-bullets li i.icon-wrench,\n.update-bullets li i.fa-wrench,.update-bullets li.fixed {color:#7AB80E;}\n.update-bullets li i.icon-remove,\n.update-bullets li i.fa-times,.update-bullets li.removed {color:#ff0000;}\n.update-bullets li.title {font-weight:bold;margin-bottom: 5px;margin-top: 5px;text-transform: uppercase;background: none;padding-left:0;}\n</style>\n\n<!-- NOTICE -->\n<h3 class=\"update-notice\"><i class=\" icon-bullhorn\"></i>NOTICE:</h3>\n<div class=\"update-content\">\n<h4 class=\"update-title\">Alterna 9 with huge upgrades and changes.</h4>\nIf you\'re using Altern old version, please don\'t update untill you had backup your site content.\nBecause of Alterna 9 with huge upgrades and changes, please read our updates details and then decided to update.\nIf you had updated Alterna 9 but you don\'t want to change your site content and want to back use old version, we also \nincludes old version (downloads files -> previous v6 version -> alterna.zip).\n</div>\n\n<div class=\"update-content\">\n<h4 class=\"update-title\">MX theme need purchaser feedback</h4>\nWe just had publish new theme named \"MX\", now collection purchaser feedback and improve it.<br/>\n<a href=\"http://goo.gl/ZDCkb6\" target=\"_blank\"><img src=\"http://themefocus.co/mx/images/preview.jpg\" alt=\"\" /></a>\n</div>\n\n<div class=\"update-content\"><h4 class=\"update-title\">VERSION 9.4.8 &ndash; July 12th, 2015</h4><ul class=\"update-bullets\">\n<li class=\"notice\"><i class=\"fa fa-bullhorn icon-bullhorn\"></i>WooCommerce 2.3.13 Ready.</li>\n\n<li class=\"updates title\">Update:</li>\n<li><i class=\"fa fa-refresh icon-refresh\"></i>Update Visual Composer Page Builder Plugin to 4.6.1</li>\n<li><i class=\"fa fa-refresh icon-refresh\"></i>Update WP Retina 2X plugin to 3.4.2.</li>\n\n</ul></div>\n\n<div class=\"update-content\"><h4 class=\"update-title\">VERSION 9.4.7 &ndash; July 6th, 2015</h4><ul class=\"update-bullets\">\n<li class=\"updates title\">Update:</li>\n<li><i class=\"fa fa-refresh icon-refresh\"></i>Update LayerSlider plugin to 5.5.</li>\n<li><i class=\"fa fa-refresh icon-refresh\"></i>Update WP Retina 2X plugin to 3.4.0.</li>\n\n</ul></div>\n\n<div class=\"update-content\"><h4 class=\"update-title\">VERSION 9.4.6 &ndash; July 3th, 2015</h4><ul class=\"update-bullets\">\n<li class=\"notice\"><i class=\"fa fa-bullhorn icon-bullhorn\"></i>Updated for Envato Auto Update WP_ERROR.</li>\n<li class=\"updates title\">Update:</li>\n<li><i class=\"fa fa-refresh icon-refresh\"></i>Update Visual Composer Page Builder Plugin to 4.6</li>\n<li><i class=\"fa fa-refresh icon-refresh\"></i>Update Envato auto update plugin to fix WP_ERROR problem.</li>\n<li><i class=\"fa fa-refresh icon-refresh\"></i>Update Team 