INSERT INTO `wp_posts` VALUES("2982", "1", "2015-07-17 13:52:30", "2015-07-17 04:52:30", "\r\n<p>B.C.1900年頃西北から侵入したセム系アムル人が古バビロニア王国を建設。</p>\r\n<p>全18世紀頃、古バビロニア最盛期の王「ハンムラビ王」がメソポタミア全土を支配し、ハンムラビ法典を制定した。</p>\r\n<p>ハンムラビ法典はメソポタミア文明の象徴であり、オリエント世界の他の民族にも広がり、旧約聖書にも影響を及ぼした。</p>\r\n<p>特徴</p>\r\n<ul>\r\n	<li>王は神の代理者として統治</li>\r\n	<li>復讐法（目には目を、歯には歯を）</li>\r\n	<li>身分法</li>\r\n</ul>\r\n<p>現在はルーブル美術館所蔵</p>\r\n<p>&nbsp;</p>\r\n", "B.C.1760 ハンムラビ法典", "", "publish", "open", "closed", "", "bc-1760-hammurabi", "", "", "2015-07-17 16:44:17", "2015-07-17 07:44:17", "", "0", "http://vccw.dev/?post_type=portfolio&#038;p=2982", "5", "portfolio", "", "0");
